// frontend/src/components/LocationPermissionPanel.js
import React from 'react';
import '../styles/LocationPermissionPanel.css'; // (opcional) estilos

const LocationPermissionPanel = ({ isOpen, onAccept, onDeny }) => {
  if (!isOpen) return null;

  const handleAccept = () => {
    if (!navigator.geolocation) {
      alert('Tu navegador no soporta geolocalización.');
      onDeny();
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        const latitude = pos.coords.latitude;
        const longitude = pos.coords.longitude;
        onAccept({ latitude, longitude });
      },
      (err) => {
        console.error('Error al obtener ubicación:', err);
        onDeny();
      },
      { enableHighAccuracy: true, timeout: 10000, maximumAge: 0 }
    );
  };

  return (
    <div className="lp-overlay">
      <div className="lp-panel">
        <h2>Permitir ubicación</h2>
        <p>
          GeoTube necesita tu ubicación para mostrar videos relevantes a tu zona.
          ¿Quieres permitir el uso de tu ubicación ahora?
        </p>
        <div className="lp-actions">
          <button className="lp-btn lp-accept" onClick={handleAccept}>Permitir</button>
          <button className="lp-btn lp-deny" onClick={onDeny}>No permitir</button>
        </div>
        <small className="lp-note">Este mensaje se muestra siempre al entrar. Puedes cambiarlo en la configuración más adelante.</small>
      </div>
    </div>
  );
};

export default LocationPermissionPanel;
